import React, { useState } from "react";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";

const UploadPage = () => {
  const [propertyType, setPropertyType] = useState(""); // 선택한 매물 유형 상태 관리
  const [selectedFile, setSelectedFile] = useState(null);

  const handlePropertyTypeChange = (event) => {
    setPropertyType(event.target.value);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    // 선택한 매물 유형(propertyType)과 다른 필요한 정보들을 서버로 업로드하는 로직을 작성하세요.
    console.log("매물 등록:", propertyType);
  };
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = () => {
    // 파일 업로드 처리 로직 작성
    // 선택된 파일(selectedFile)을 서버로 업로드할 수 있습니다.
    // 업로드 후 필요한 처리를 수행할 수 있습니다.
  };

  return (
    <>
      <Header />
      <h2>내 방 올리기</h2>

      <form onSubmit={handleSubmit}>
        <label>
          <input
            type="radio"
            name="propertyType"
            value="매매"
            checked={propertyType === "매매"}
            onChange={handlePropertyTypeChange}
          />
          매매
        </label>

        <label>
          <input
            type="radio"
            name="propertyType"
            value="전세"
            checked={propertyType === "전세"}
            onChange={handlePropertyTypeChange}
          />
          전세
        </label>

        <label>
          <input
            type="radio"
            name="propertyType"
            value="원룸"
            checked={propertyType === "원룸"}
            onChange={handlePropertyTypeChange}
          />
          원룸
        </label>
        <br />
        <input type="file" onChange={handleFileChange} />
        <br />
        <br />
        <button type="submit">매물 등록</button>
      </form>

      <Footer />
    </>
  );
};

export default UploadPage;
