import React, { useState } from "react";
import Header from "../../components/Header/Header";
import Footer from "../../components/Footer/Footer";
import DaumPostcode from "react-daum-postcode";

const UploadPage = () => {
  const [propertyType, setPropertyType] = useState(""); // 선택한 매물 유형 상태 관리
  const [selectedFile, setSelectedFile] = useState(null);
  const [address, setAddress] = useState("");

  const handlePropertyTypeChange = (event) => {
    setPropertyType(event.target.value);
  };
  //@@계약 종류 선택 기능
  const handleSubmit = (event) => {
    event.preventDefault();
    // 선택한 매물 유형(propertyType)과 다른 필요한 정보들을 서버로 업로드하는 로직을 작성하세요.
    console.log("매물 등록:", propertyType);
  };
  //@@주소 검색 기능
  const handleAddressSearch = () => {
    // 주소 검색 모달 열기
  };
  const handleAddressSelect = (data) => {
    // 선택한 주소 처리
    const fullAddress = data.address; // 전체 주소
    setAddress(fullAddress);
  };
  //@@사진 이미지 업로드 기능
  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
  };

  const handleUpload = () => {
    // 파일 업로드 처리 로직 작성
    // 선택된 파일(selectedFile)을 서버로 업로드할 수 있습니다.
    // 업로드 후 필요한 처리를 수행할 수 있습니다.
  };

  return (
    <>
      <Header />
      <h2>내 방 올리기</h2>
      <h5>계약 방식</h5>
      <form onSubmit={handleSubmit}>
        <label>
          <input
            type="radio"
            name="propertyType"
            value="매매"
            checked={propertyType === "매매"}
            onChange={handlePropertyTypeChange}
          />
          매매
        </label>

        <label>
          <input
            type="radio"
            name="propertyType"
            value="전세"
            checked={propertyType === "전세"}
            onChange={handlePropertyTypeChange}
          />
          전세
        </label>

        <label>
          <input
            type="radio"
            name="propertyType"
            value="원룸"
            checked={propertyType === "원룸"}
            onChange={handlePropertyTypeChange}
          />
          원룸
        </label>
        <br />
        <br />
        <button type="button" onClick={handleAddressSearch}>
          주소 검색
        </button>
        <p>선택한 주소: {address}</p>

        {/* 주소 검색 모달 */}
        <DaumPostcode onComplete={handleAddressSelect} autoClose={true} />
        <br />
        <br />
        <input type="file" onChange={handleFileChange} />
        <br />
        <br />
        <button type="submit">매물 등록</button>
      </form>

      <Footer />
    </>
  );
};

export default UploadPage;
